syntax.programlisting:
>
SET [GLOBAL | SESSION] TRANSACTION
    <replaceable>transaction_characteristic</replaceable> [, <replaceable>transaction_characteristic</replaceable>] ...

<replaceable>transaction_characteristic</replaceable>: {
    ISOLATION LEVEL <replaceable>level</replaceable>
  | <replaceable>access_mode</replaceable>
}

<replaceable>level</replaceable>: {
     REPEATABLE READ
   | READ COMMITTED
   | READ UNCOMMITTED
   | SERIALIZABLE
}

<replaceable>access_mode</replaceable>: {
     READ WRITE
   | READ ONLY
}

>
{EXPLAIN | DESCRIBE | DESC}
    <replaceable>tbl_name</replaceable> [<replaceable>col_name</replaceable> | <replaceable>wild</replaceable>]

{EXPLAIN | DESCRIBE | DESC}
    [<replaceable>explain_type</replaceable>]
    {<replaceable>explainable_stmt</replaceable> | FOR CONNECTION <replaceable>connection_id</replaceable>}

<replaceable>explain_type</replaceable>: {
    EXTENDED
  | PARTITIONS
  | FORMAT = <replaceable>format_name</replaceable>
}

<replaceable>format_name</replaceable>: {
    TRADITIONAL
  | JSON
}

<replaceable>explainable_stmt</replaceable>: {
    SELECT statement
  | DELETE statement
  | INSERT statement
  | REPLACE statement
  | UPDATE statement
}

